prisma-model.model-definition:

model Asset {
  // Core identification
  id            String   @id @default(uuid())
  assetTag      String   @unique
  serialNumber  String?
  description   String

  // Status and condition
  status        AssetStatus      @default(ACTIVE)
  condition     AssetCondition   @default(GOOD)

  // TO BE ADDED: User-provided fields will go here
  // Examples:
  // manufacturer  String?
  // model         String?
  // purchaseDate  DateTime?
  // purchasePrice Decimal?
  // location      String?
  // department    String?

  // Relationships
  projectId     String?
  project       Project?         @relation(fields: [projectId], references: [id])

  assignedUserId String?
  assignedUser   User?           @relation("AssignedAssets", fields: [assignedUserId], references: [id])

  createdById   String
  createdBy     User            @relation("CreatedAssets", fields: [createdById], references: [id])

  // Timestamps
  createdAt     DateTime         @default(now())
  updatedAt     DateTime         @updatedAt

  // Indexes for performance
  @@index([status])
  @@index([assignedUserId])
  @@index([projectId])
  @@index([createdAt])
}

step-1-database.schema:

model Asset {
  id        String   @id @default(uuid())
  assetTag  String   @unique
  name      String

  // That's it! No relations, no extra fields yet

  @@map("assets")
}

step-2-backend.service:

@Injectable()
export class AssetsService {
  constructor(private prisma: PrismaService) {}

  async findAll() {
    return this.prisma.asset.findMany();
  }

  async create(assetTag: string, name: string) {
    return this.prisma.asset.create({
      data: { assetTag, name }
    });
  }
}

step-2-backend.controller:

@Controller('api/assets')
export class AssetsController {
  constructor(private service: AssetsService) {}

  @Get()
  async findAll() {
    return this.service.findAll();
  }

  @Post()
  async create(@Body() body: { assetTag: string; name: string }) {
    return this.service.create(body.assetTag, body.name);
  }
}

step-4-frontend.component:

export function Assets() {
  const [assets, setAssets] = useState([]);
  const [tag, setTag] = useState('');
  const [name, setName] = useState('');

  useEffect(() => {
    fetch('/api/assets')
      .then(res => res.json())
      .then(setAssets);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await fetch('/api/assets', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ assetTag: tag, name })
    });
    window.location.reload(); // Simple refresh
  };

  return (
    <div>
      <h1>Assets</h1>
      <form onSubmit={handleSubmit}>
        <input value={tag} onChange={(e) => setTag(e.target.value)} required />
        <input value={name} onChange={(e) => setName(e.target.value)} required />
        <button>Add</button>
      </form>
      <ul>
        {assets.map(a => <li key={a.id}>{a.assetTag} - {a.name}</li>)}
      </ul>
    </div>
  );
}
